  const localName =
    importDeclaration.find(j.Identifier)
      .get(0)
      .node.name;

  // find where `.factory` is being called
  return root.find(j.CallExpression, {
    callee: {
      type: 'MemberExpression',
      object: {
        name: localName,
      },
      property: {
        name: 'factory',
      },
