var describe = require('jscodeshift-helper').describe;
import { API } from 'jscodeshift'

export default function transformer(file: any, api:API) {
  const j = api.jscodeshift;
  const root = j(file.source);

  // Find all occurrences of z.string() with a description property
  root.find(j.CallExpression, {
    callee: {
      type: 'MemberExpression',
      object: { name: 'z' },
      property: { name: 'string' },
    },
    // @ts-ignore
    // arguments: [{
    //   type: 'ObjectExpression',
    //   properties: [{
    //     key: { name: 'description' },
    //   }],
    // }],
  }).forEach(path => {
    // Remove the description property
    // @ts-ignore
    const descriptionProperty = path.value.arguments[0].properties[0];
    j(path).replaceWith(j.callExpression(
      j.memberExpression(j.identifier('z'), j.identifier('string')),
      [descriptionProperty.value]
    ));
  }).toSource()

}

